    private int GetNum(string str)
    {
        int num = 0;
        int gap = 0;
        for (int i = 0; i < str.Length; i++)
        {
            gap = str[i] - '0';
            if (gap < 0 || gap >= 10) return -1;
            num = num * 10 + gap;
        }
        return num;
    }
