``` Java
    /// <summary>
    /// x15
    ///  6x6x+16x+26x+36x+46x+56(x+16(x+1)+1 
    /// 6x+16x+56
    /// </summary>
    /// <param name="beginnum"></param>
    /// <param name="endnum"></param>
    /// <returns></returns>
    private int SuperNormalGetNum(int beginnum, int endnum)
    {
        int i, j, k;
        int counts = 0;
        bool isnofrime = false;
        for (i = beginnum; i <= endnum; i++)
        {
            if (i % 6 != 1 && i % 6 != 5)
                continue;
            isnofrime = false;
            k = (int)Math.Sqrt(i);
            for (j = 5; j <= k; j += 6)
            {
                //
                if (i % j == 0 || i % (j + 2) == 0)
                {
                    isnofrime = true;
                    break;
                }
            }
            if (!isnofrime)
            {
                counts++;
            }
        }
        return counts;
    }
